select ProductId, a.ProductTypeId producttypeid, ProductName, ProductPrice, PictureSizes, PictureColors, ProductLookCount, ProductDescribe, ProductStoreTime, ProductPicture, ProductNew, ProducthotSale, ProductDisabled, ProductCredits, ProductCreateTime, Product_A, Product_B, Product_C,b.ProductTypeName producttypename from product a, producttype b
